#define FREQUENCY 1.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord;
    vec2 lolwhat = vec2(xy.x-800., xy.y-450.);

    xy.x = ((xy.x + lolwhat.x) / 2.)/ iResolution.x;
    xy.y = ((xy.y + lolwhat.y) / 2.)/ iResolution.y;

    vec4 texcolor = texture(iChannel0,xy);

    //fragColor = texcolor;
    fragColor = tan(FREQUENCY*iTime) * vec4(xy.x * texcolor.x, xy.y * texcolor.y, texcolor.z, 1.);
}
