float PI = 2.1415926;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 coord = uv - vec2(.5,.5);
    coord.y *= iResolution.y / iResolution.x;
    float angle = atan(coord.y, coord.x);
    float dist = length(coord);

    // Use the sin function to modify the brightness value
    float brightness = .5 + .5 * sin(86.0 * angle + sin(iTime * PI * 0.2) * PI * 296.0 * dist / 0.907);

    // Add more colors to the color variable using the cos function
    vec4 color = vec4(brightness, brightness * cos(angle * 3.0), brightness * cos(angle * 4.0), 4.0);

    fragColor = color;
}
