void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = textureLod(iChannel0, uv * 0.125f, 0.0).rgba;

    color.rgb *= color.a;

    fragColor = vec4(pow(color.rgb, vec3(1.0f/2.2f)),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //uv = floor(uv * 256.0f) / 1024.0f;
    uv += iTimeDelta*0.1f;

    float bn = textureLod(iChannel1, fragCoord/1024.0f, 0.0f).r;
    vec4 d0 = textureLod(iChannel0, uv, 0.0f);

    float d = 0.0f;
    float tt = 0.0f;

    tt = 0.49f*(d0.x);//abs(sin(float(iTime)*4.0f)) * 0.5f + 0.5f;

    d += mix(textureLodOffset(iChannel0, uv, 0.0f, ivec2( 0, 1)).r, 1.0f - d0.x, tt);
    d += mix(textureLodOffset(iChannel0, uv, 0.0f, ivec2( 0,-1)).r, 1.0f - d0.x, tt);
    d += mix(textureLodOffset(iChannel0, uv, 0.0f, ivec2( 1, 0)).r, 1.0f - d0.x, tt);
    d += mix(textureLodOffset(iChannel0, uv, 0.0f, ivec2(-1, 0)).r, 1.0f - d0.x, tt);

    if (d0.x+d0.y > 1.0f) {
        d = 0.0f;
    }
    else if (d > (3.0f + d0.x)) {
        d = 0.0f;
    }
    else if (d < 0.5f) {
        d = tt + 0.5f;
    }
    else {
        d *= d;
        d *= bn * d0.x;
    }

    if ( iFrame < 100 ) {
        d = bn;
    }

    d0.w = mix(d0.z, d0.w, d);
    d0.z = mix(d0.y, d0.z, d);
    d0.y = mix(d0.x, d0.y, d);

    fragColor = vec4(d, d0.y, d0.z, d0.w);
}
