// Ideas taken from The Art of Code tutorial https://www.youtube.com/watch?v=GgGBR4z8C9o


float Circle (vec2 uv, vec2 p, float r, float blur)
{
    float c;
    float d = length(uv-p);

    //if(d<r) c = 1.0; else c =0.0;

    c = smoothstep(r,r-blur,d);
    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;    // uv = 0 to 1
    uv = uv - 0.5;                         // bring origin to center of screen
    uv.x *= iResolution.x / iResolution.y; 

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(2,0,2));

    //float d = length(uv); // magnitude
    //float r = 0.3;
    //float c;

    //c = smoothstep(r,r-0.02,d);
    //if(d<r) c=1.0; else c=0.0;

    //vec3 col = vec3(c,0.0,0.0);
    float mask;
    vec3 col;

    float face = Circle(uv, vec2(0.0,0.0), 0.4, 0.01);

    float eyeL = Circle(uv, vec2(-0.17,0.2), 0.08, 0.01);
    float eyeR = Circle(uv, vec2(0.17,0.2), 0.08, 0.01);
    float nose = Circle(uv, vec2(0.0,0.0), 0.04, 0.01);


    float mouth = Circle(uv, vec2(0.0,-0.05), 0.25, 0.01);
    mouth -= Circle(uv, vec2(0.0,0.05), 0.25, 0.01);

    if(eyeL>0.0) mouth =0.0;
    if(eyeR>0.0) mouth =0.0;
    if(nose>0.0) mouth =0.0;


    mask = mouth + eyeL + eyeR + nose;

    //////mask += 2.0*eyeR;
    //////mask += 2.0f*eyeL;

    face = face - mask;




    vec3 col_eye = vec3(0.0,0.0,1.0)*(eyeL+eyeR);
    vec3 col_mouth = vec3(1.0,0.0,0.0)*(mouth);
    vec3 col_nose = vec3(1.0,0.0,1.0)*(nose);
    vec3 col_face = vec3(1.0,1.0,0.0)*(face);   

    col += col_face + col_eye + col_mouth + col_nose ;
    //col = vec3(0.0,0.0,1.0)*mask;

    // Output to screen
    fragColor = vec4(col,1.0);
}
