float speedX = 0.5;
float speedY = 0.5;
bool isVertical = false;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
    //
    if(isVertical){
        // horizontal scroll
        col = texture(iChannel0,uv+vec2(iTime*speedX,0.0*speedY)).xyz;
    }else{
        // vertical scroll
        col = texture(iChannel0,uv+vec2(0.0*speedX,iTime*speedY)).xyz;
    }


    // Output to screen
    fragColor = vec4(col.x,0.0,0.0,1.0);
}
