//to use dissolve by color-> change key condition
//transition start in 5 seconds 

float percent = 0.0;

const float initialTime = 5.0;

const float timeTaken = 3.0; //s

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   // Normalized pixel coordinates (from 0 to 1)
   vec2 uv = fragCoord/iResolution.xy;

   //get video input as texture
   vec4 vid1 = texture(iChannel0,uv);
   vec4 vid2 = texture(iChannel1,uv);


   // Output to screen
   vec3 col = vec3(0.0,0.0,0.0);
   col = vid1.rgb;


   if(iTime>initialTime){
    percent = (iTime-initialTime)/timeTaken; 
   }

   float key = vid1.g; //use as red


   if(key> (1.0-percent)){
       col = vid2.rgb;
   }

    // Output to screen
    fragColor = vec4(col,1.0);
}
