float f(float x) {
    return sin(5.0 * x) * cos(3.0 * x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.x = 5.0 * uv.x - 2.5;
    uv.y = 4.0 * uv.y - 1.5;

    fragColor = vec4(1.0);

    fragColor = (fract(uv.x) < 0.01) ? vec4(0.9) : fragColor;
    fragColor = (fract(uv.y) < 0.01) ? vec4(0.9) : fragColor;

    fragColor = (abs(uv.x) < 0.01) ? vec4(0.0) : fragColor;
    fragColor = (abs(uv.y) < 0.01) ? vec4(0.0) : fragColor;

    fragColor = (abs(f(uv.x) - uv.y) < 0.02) ? vec4(1.0, 0.0, 0.0, 1.0) : fragColor;
}
