float PI = 3.14;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;


    float theta = mod( (atan(uv.y,uv.x)+PI)/(2.*PI), .3);
    vec4 spect = texture(iChannel0, vec2(theta,0.0));
    vec4 col;
    vec3 circle;
    for (float i=0.; i<150.; i+=8.){
        circle = vec3(mod(.5*iTime+i*.01, 3.5) , mod(.5*iTime+(i*.01+.03), 3.5), mod(.5*iTime+(i*.01+.04), 3.5)) + 1.3*spect.x - log(length(uv*17.));
        col += vec4( vec3(.031/circle), 0) ;
    } 

    fragColor = col;
}
