vec3 iColor = vec3(1., 0.5, 0.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    // 调整半径
    float r = length(p) * 1.4;
    // 颜色在这里设置
    vec3 color =iColor;

    float timer=iTime / 1.2;
    float a = pow(r, 2.0);
    float b = sin(r * 0.8 - 1.6);
    float c = sin(r - 0.010);
    float s = sin(a - timer * 3.0 + b) * c;

    color *= abs(1.0 / (s * 10.8)) - 0.01;
    // 限制输出值
    color.x = color.x>iColor.x?iColor.x:color.x;
    color.y = color.y>iColor.y?iColor.y:color.y;
    color.z = color.z>iColor.z?iColor.z:color.z;

    fragColor = vec4(color, 1.);
}
