void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;   
    float time = iTime;

    float reduceTime = fract(time);
    vec4 baseColor = texture(iChannel0, uv);
    vec4 frameColor;
    if (reduceTime < 0.25) {
        frameColor = texture(iChannel1, uv);
    } else if (reduceTime < 0.5) {
        frameColor = texture(iChannel2, uv);
    } else {
        frameColor = texture(iChannel3, uv);
    }


    fragColor = colorEadgeBlend(baseColor, frameColor);
}

vec4 overlayBlend(vec4 baseColor, vec4 blendColor) {
	vec4 lumCoeff=vec4(0.2125,0.7154,0.0721,1.0);
	float luminance = dot(baseColor.rgb,lumCoeff.rgb);
	if(luminance < 0.45)
	{
	    return 2.0 *baseColor * blendColor;
	}
	else if(luminance >0.55)
	{
	    return vec4(1.0)-2.0* ((vec4(1.0)-baseColor)*(vec4(1.0)-blendColor));
	}
	else
	{
	    vec4 colorT1 =  2.0 *baseColor * blendColor;
	    vec4 colorT2 =  vec4(1.0)-2.0* ((vec4(1.0)-baseColor)*(vec4(1.0)-blendColor));
	    return  mix(baseColor,blendColor,(luminance-0.45)*10.);
	}
}

vec4 colorEadgeBlend(vec4 baseColor, vec4 blendColor) {
    return baseColor/(vec4(1.0)-blendColor);
}
