vec2 Position = vec2(0.0,0.0);
float Radius = 0.5;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    //Without changing colors
    //vec4 gradientColor = vec4(1.0 - length(Position - uv),0,1,1);
	vec4 gradientColor = vec4(1.0 - length(Position - uv)-(1.-Radius),0.,0,1.0);


	fragColor = gradientColor;

}
