#define t floor(iTime*140./60.)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Variables
    vec4 vid1 = texture(iChannel0, uv*vec2(.6,1));
    vec4 vid2 = texture(iChannel1, uv*vec2(.6,1));
    vec4 vid3 = texture(iChannel2, uv*vec2(1,1));
    //Split Screen Effect 
    if(uv.x > sin(t)+.75){
        fragColor = vid2;
        }else if(uv.x < sin(t)+.25){
            fragColor = vid1;  
        }else
            fragColor = vid3;

}
