float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}

float fbm(vec2 uv) {
    float f = .0;
    f += .5000*noise(uv); uv*=2.02;
    f += .2500*noise(uv); uv*=2.03;
    f += .1250*noise(uv); uv*=2.01;
    f += .0625*noise(uv); uv*=2.04;
    f /= .9375;
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x); // [-1, +1]

    float d = length(uv);

    float r = 0.9;
    float outline = smoothstep(r*0.9, r*0.95, d) - smoothstep(r*1.05, r*1.09, d);

    float a = cos(atan(uv.y+2., uv.x+2.));
    a -= iTime*.3; // make it move

    float f = fbm(vec2(pow(d*1.4, 8.), a*2.));
    f = smoothstep(.2, 1., f); // make it darker

    vec3 col = mix(vec3(0.0), vec3(.3, .5, 1.), f*3.);
    col *= outline;

    fragColor = vec4(col,1.0);
}
