#ifdef GL_ES
precision mediump float;
#endif




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = fragCoord.xy/iResolution.xy;
  st.x *= (iResolution.x/0.45)/(iResolution.y/0.25);
  vec3 color = vec3(0.0);
  float d = 0.0;
  float d1 = 0.0;
  float d2 = 0.0;

  // Remap the space to -1. to 1.
  st = st *2.-1.;

  // Make the distance field
  d = length( abs(st)-.003*atan(iTime) );
  d1 = length( max(abs(st)-.09/sin(iTime*0.1),0.001) );
  d2 = length( min(abs(st)-.09*tan(iTime*0.1),1.1) );
  // Visualize the distance field
  fragColor = vec4(vec3(abs(tan(d2/d1*2.0*d)),acos(sin(d2/d1*10.0*d)),tan(d2/d1*13.0*d)),1.0);


}
