vec3 rotateZ(vec3 center, vec3 p, float rot ){
    vec3 cp = p - center;
    return vec3(
            cp.x*cos(rot)-cp.y*sin(rot),
            cp.x*sin(rot)+cp.y*cos(rot),
            cp.z*1.
        ) + center;
}

// https://iquilezles.org/articles/distfunctions2d
float sdLine(vec2 p, vec2 a, vec2 b){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
    // return abs((b.x-a.x)*(a.y-p.y)-(a.x-p.x)*(b.y-a.y))/length(b-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 tr = vec3((iMouse.x-0.5*iResolution.x)/iResolution.y , 
                   (iMouse.y-0.5*iResolution.y)/iResolution.y, 
                   0 );
    vec3 p = vec3(0.3 , 0.3, 0)+tr;

    p = rotateZ(tr, p, iTime*1.);

    float d = length(vec3(uv,0) - p); 
    vec3 col = vec3(d/3.);
    if (d < .1) col = vec3(1);


    d = sdLine(uv,vec2(tr.x,tr.y),vec2(p.x,p.y));
    if (d < 0.01) col = vec3(1,0,0);  // red line

    // Output to screen
    fragColor = vec4(col,1.0);
}
