void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float angle = 0.2 + iTime * 0.3;

    uv = uv/dot(uv,uv)+(sin(iTime*0.1))+iTime*0.1;

    for(float i = 1.0; i < 16.0; i += 1.0){
        uv = abs(uv);
        uv -= 0.5;
        uv *= 1.1;
        uv *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }

    fragColor = sin(9.0*uv.yxxy);
}
