#define ACCENT_COLOR             vec3(0.156, 0.521, 0.105)
#define COLOR_THRESHOLD          0.9999
#define EFFECT_SCALE             1.0

float getLuminance(in vec3 color)
{
    return dot(color, vec3(0.2126, 0.7152, 0.0722));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 pixelColor = texture(iChannel0, uv).rgb;
    vec3 outputColor;
    const vec3 desiredColor = ACCENT_COLOR;
    const float threshold = COLOR_THRESHOLD;
    const float effectScale = EFFECT_SCALE;

    vec3 monochrome = vec3(getLuminance(pixelColor));
    vec3 probeColor = normalize(pixelColor + vec3(0.001)); // to avoid normalizing null vector
    float t = dot(probeColor, normalize(desiredColor + vec3(0.001)));
    float f = smoothstep(threshold - 0.1, threshold, t);
    vec3 b = mix(pixelColor, monochrome, effectScale);
    outputColor = mix(b, pixelColor, f * step(0.0, uv.x));
    fragColor = vec4(outputColor, 1.0);
}
