vec2 scalePointByCenter(vec2 point, vec2 center, float factor) { 
    vec2 dis = point - center;
    return center + dis * factor;
}

float scaleValueByCenter(float val, float center, float factor) {
    float dis = val - center;
    return dis * factor + center;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float range = 2.0;
    vec2 center = vec2(0.5);

    vec2 uv = fragCoord/iResolution.xy;
    uv.y = scaleValueByCenter(uv.y, 0.5, iResolution.y / iResolution.x);
    uv = scalePointByCenter(uv, center, distance(center, uv) * range);

    fragColor = texture(iChannel0, uv);
}
