void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 st = fract(uv)*5.-352.;
	float c = 1.0;
	float tr = .005;
    float speed = .9;
    float iter = 9.;

    for (float n = 0.; n < iter; n++) 
	{
        float t = iTime/(n*2.+1.)*speed;
        float a = t+st.x;
        float b = t+st.y;
		st +=  vec2(cos(b) + sin(a), sin(a) + cos(a));
		c += 1.0/length(vec2(st.x / (sin(a)/tr),st.y / (cos(b)/tr)));
	}
	c /= iter;
    c*=3.;
	vec3 col = vec3(pow(c, 122.));
    col = mix(vec3(0,0.3,.4)*2.,vec3(1),col);
	fragColor = vec4(col, 1.0);
}
