void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv2 = fragCoord/iResolution.xy;
    float wiggle = sin(iTime*5.0+(uv2.y*5.0)) * -0.01;

    vec2 centralpoint = vec2(uv2.x*1.5f - .75, uv2.y-0.5);
    float ball = length(centralpoint);



    if(ball<0.3+wiggle)
    fragColor = vec4(1,0,0,1.0);
    if(ball>0.3+wiggle)
    fragColor = vec4(1,1,1,1.0);
}
