int BOX_SIZE = 10;

vec4 tx(vec2 uv, vec2 l){
    return texture(iChannel0, uv + l/iResolution.xy);
}

vec4 dilate(vec2 uv){
    // This uses box as a structuring element
    // Triangle may be constructed by starting with j=i
    vec4 maxColor = vec4(0.0);
    int B_SIZE = BOX_SIZE/2;
    for(int i=-B_SIZE;i<B_SIZE;i++){
        for(int j=-B_SIZE;j<B_SIZE;j++){
           maxColor = max(tx(uv, vec2(i,j)), maxColor);
        }
    }
    return maxColor;
}

vec4 erode(vec2 uv){
    // Erosion is same as dilation except we now
    // minimize instead of maximizing
    vec4 minColor = vec4(1.0);
    int B_SIZE = BOX_SIZE/2;
    for(int i=-B_SIZE;i<B_SIZE;i++){
        for(int j=-B_SIZE;j<B_SIZE;j++){
           minColor = min(tx(uv, vec2(i,j)), minColor);
        }
    }
    return minColor;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    if(uv.x<0.5)
        fragColor = dilate(uv);
    else
        fragColor = erode(vec2(uv.x-0.5, uv.y));
}
