float scale = 5.0;
float strength = 3.0;
float border_thickness = 0.02;

// Inspired by: https://www.shadertoy.com/view/wdVcDt
float flat_topped_hexagon_sdf(vec2 p) {
    p.x *= 0.57735*2.0;
    p.y += mod(floor(p.x), 2.0) * 0.5;
    p = abs((mod(p, 1.0) - 0.5));
    float v = max(p.x * 1.5 + p.y, p.y * 2.0) - 1.0;
    return abs(v * strength);
}

// From: https://www.shadertoy.com/view/Md2GDz
bool flat_topped_hexagon_border(vec2 p) {
    p.x *= 0.57735*2.0;
    p.y += mod(floor(p.x), 2.0)*0.5;
    p = abs((mod(p, 1.0) - 0.5));
    return abs(max(p.x*1.5 + p.y, p.y*2.0) - 1.0) > border_thickness;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 normalized_coords = fragCoord.xy / iResolution.y;

    fragColor.r = flat_topped_hexagon_sdf(normalized_coords * scale);
    fragColor.g = float(flat_topped_hexagon_border(normalized_coords * scale));
    fragColor.a = 1.0;
}
