// 高斯模糊半径
float RADIUS = 1.0;
// 因为uv的坐标系为0-1，这里为了代入高斯模糊半径计算，需要缩放到对应的量级
float size = 30000.0;
float PI = 3.14159;

// PDF
float gaussianBlur (float sigma, float x, float y) {
    return (exp(-pow(x, 2.0) - pow(y, 2.0)) / 2.0 * pow(sigma, 2.0)) / (2.0 * pow(sigma, 2.0) * PI); 
}

// 权重乘以当前获取的像素值
vec4 computedBlur (float gauss, vec4 inputColor) {
        return (inputColor * gauss);
}

// 高斯卷积核计算
vec4 blurColor (vec2 uv) {
    // 初始色值
    vec4 color = vec4(0.0);
    //
    float sum = 0.0;

    // 进行二维矩阵遍历
    for (float x = -RADIUS; x <= RADIUS; x++) {
        for (float y = -RADIUS; y <= RADIUS; y++) {
            // 计算对应的纹理坐标
            vec2 target = uv + vec2(x / size, y / size);
            // 计算出高斯权重
            float gauss = gaussianBlur(1.5, target.x, target.y);
            // 获取加权后的色值并累加
            color += computedBlur(gauss, vec4(vec3(texture(iChannel0, target).x), 1.0));
            // 计算加权概率之和
            sum += gauss;
        }
    }
    // 这里放在循环外统一进行概率归一化处理，提高计算效率
    color /= sum;
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = blurColor(uv);

    fragColor = color;
}
