void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch(iChannel0,ivec2(U),0);
}

float dCircle(vec2 uv, vec2 p, float r){
    return length(uv-p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-(iResolution.xy*.5))/iResolution.y;
    vec2 mPos = (iMouse.xy-(iResolution.xy*.5))/iResolution.y;
    vec2 mClick = (iMouse.zw-(iResolution.xy*.5))/iResolution.y;
    vec2 pos = mPos.xy;
    float freq = 1.;
    float d = 0.3;
    pos.x += d*sin(freq*iTime);
    pos.y += d*cos(freq*iTime);
    float r = 0.01;
    float circle = smoothstep(.001,0.,dCircle(uv, pos, r));
    vec4 buffer = texture(iChannel0,fragCoord/iResolution.xy);
    vec3 col = vec3(circle);


    float speed = 0.1;
    float fade = pow((1.0-speed),0.1);
    buffer.rgb = max(buffer.rgb*fade, col);

    fragColor = buffer;
}
