const int levels = 5;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;



    // EDGE DETECTION

    vec4 edge_x;
    vec4 edge_y;

    for (int dx = -1; dx <= 1; dx++) {
    for (int dy = -1; dy <= 1; dy++) {
        vec2 uv = (fragCoord + vec2(dx,dy)) / iResolution.xy;

        vec4 pixel = texture(iChannel0, uv);

        edge_x += float(dx + dx * abs(dy)) * pixel;
        edge_y += float(dy + dy * abs(dx)) * pixel;
    }
    }

    float edge_x_max = max(edge_x.r, max(edge_x.g, edge_x.b));
    float edge_y_max = max(edge_y.r, max(edge_y.g, edge_y.b));

    float edge = edge_x_max * edge_x_max + edge_y_max * edge_y_max;
    edge = smoothstep(.04,.09,edge);
    // result: mostly black, but white along edges



    // BRIGHTNESS QUANTIZATION

    vec4 pixel = texture(iChannel0, uv);
    float grey = max(pixel.r, max(pixel.g, pixel.b));
    float factor = ceil(grey * float(levels)) / float(levels);
    vec3 color = pixel.rgb * factor;



    // PUTTING IT ALL TOGETHER

    fragColor = vec4(color - edge, 1.);
}
