// golfed from https://www.shadertoy.com/view/ftffDB

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    float t = abs(mod(iTime + 5., 10.) - 5.),  // animation curve 
    a = floor(t);                              // steps for uv switching
    t -= a;                                    // t = fract(t); 
    t *= t * (3. - t - t);                     // t = smoothstep(0., 1., t);
    O = vec4(
        .2 - t * .1                            // radius of circles
        >                                      // 1. - step(radius, sdf)
        length(abs(fract(                      // sdf for circles
            pow(2., a)                         // pot uv division
            * 
            ((U - .5 * R) / R.y - .5)) - .5)   // centring uv
            - 
            t / 4.)                            // diagonal movment animation
    );
}
