#define pi 3.1415926
vec4 head(vec2 uv){
    vec4 col = vec4(.9, .65, .1, 1.);
    float d = length(uv);
    col.a = smoothstep(0.5,0.49,d+0.12);

    float edgeshadow = smoothstep(0.35,0.49,d+0.12);
    col.rgb *= 1.-edgeshadow*.05;

    float edgeline = smoothstep(0.485,0.49,d+0.12);
    col.rgb = mix(col.rgb,vec3(.9,.55,.3),edgeline);

    float highlight = smoothstep(0.49,0.4,d+0.08);
    highlight *= clamp(0.4,0.42,uv.y+0.25); 
    col.rgb = mix(col.rgb,vec3(1.),highlight);

    return col;
}

vec4 pupil(vec2 uv, float x, float y){
    vec4 col = vec4(0.);

    float d = length(uv-vec2(x,y));
    col.a = smoothstep(0.5,0.49,d+0.44);
    return col;
}

vec4 eye(vec2 uv, float x, float y){
    vec4 col = vec4(1.);
    uv.y *= iResolution.y/iResolution.x + 0.25;
    float d = length(uv-vec2(x-0.01,y));
    col.a = smoothstep(0.5,0.495,d+0.415);
    return col;
}

vec4 mouth(vec2 uv, float l, float r, float b, float t){
    vec4 col = vec4(.4,.23,.1,0.);   
    uv.y += sin(32.*uv.x+12.*iTime)*.02;
    float band1 = smoothstep(l,r,uv.x);
    float band2 = smoothstep(-l,-r,uv.x);
    float bandx = band1+band2;

    float band3 = smoothstep(b,t,uv.y+0.15);
    float band4 = smoothstep(-b,-t,uv.y+0.15);
    float bandy = band3+band4;
    col.a = bandx+bandy;

    return col;
}

vec4 eyebow(vec2 uv, float l, float r, float b, float t, float movephase){
    vec4 col = vec4(.4,.23,.1,0.);   
    uv.y += uv.x + .02*pow(sin(4.*iTime+movephase),2.);
    float band1 = smoothstep(l,r,uv.x);
    float band2 = smoothstep(-l,-r,uv.x);
    float bandx = band1+band2;

    float band3 = smoothstep(b,t,uv.y+0.15);
    float band4 = smoothstep(-b,-t,uv.y+0.15);
    float bandy = band3+band4;
    col.a = bandx+bandy;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 back = vec3(1.);
    vec4 col = vec4(0.); 
    vec4 head1 = head(uv);
    vec4 eyebow1 = eyebow(uv-vec2(.15,.42),0.045,0.05,0.008,0.012,.5*pi);
    vec4 eyebow2 = eyebow(vec2(-uv.x-.2,uv.y-.38),0.045,0.05,0.008,0.012,pi);
    vec4 eye1 = eye(uv,.10,.09);
    vec4 eye2 = eye(uv,-.13,.09);
    vec4 pupil1 = pupil(uv,.11,.08);
    vec4 pupil2 = pupil(uv,-.12,.08);
    vec4 mouth1 = mouth(uv-vec2(0.,-0.05),0.21,0.22,-0.008,-0.012);

    col.rgb = back;
    col.rgb = mix(col.rgb,head1.rgb,head1.a);

    col.rgb = mix(col.rgb,eye1.rgb,eye1.a);
    col.rgb = mix(col.rgb,eye2.rgb,eye2.a);
    col.rgb = mix(col.rgb,pupil1.rgb,pupil1.a);
    col.rgb = mix(col.rgb,pupil2.rgb,pupil2.a);

    float eyebow11 = clamp(0.,1.,eyebow1.a);
    col.rgb = mix(eyebow1.rgb,col.rgb,eyebow11);
    float eyebow22 = clamp(0.,1.,eyebow2.a);
    col.rgb = mix(eyebow2.rgb,col.rgb,eyebow22);

    //float mouth11 = smoothstep(0.,1.,mouth1.a);
    float mouth11 = clamp(0.,1.,mouth1.a);
    col.rgb = mix(mouth1.rgb,col.rgb,mouth11);
    // Output to screen
    fragColor = col;
}
