// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan, slerpy, wwrighter 
// BigWings and FabriceNeyret for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  
// https://twitter.com/CookieDemoparty


#define TAU 6.283183
#define PI acos(-1.)
#define ITER 64. 

#define BPM (160./60.)
#define dt(sp,off) fract((iTime+off)*sp)
#define easeout(s,of) easeOutExpo(abs(-1.+2.*dt(s,of)))

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define crep(puv,c,l) puv=(puv-c*clamp(round(puv/c),-l,l))
#define pal(t,c,d) (vec3(0.5)+vec3(0.5)*cos(TAU*(c*t+d)))

#define od(puv,d) (dot(puv,normalize(sign(puv)))-d)
#define sphe(puv,d) (length(puv)-d) 

float hash21(vec2 x)
{return fract(sin(dot(x,vec2(13.4,43.4)))*124.4);}

float easeOutExpo (float x)
{return x == 1. ? 1. : 1. - exp2(-10. * x);}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
  return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float candy; vec2 cid;
float SDF (vec3 p)
{
  p.yz *= rot(-atan(1./sqrt(2.)));
  p.xz *= rot(PI/4.);

  vec2 per = vec2(2.);

  vec3 ppp = p;
  cid=round(ppp.xy/per); 
  crep(ppp.xy,per,3.);
  float size = easeout(BPM/2.,(cid.x+cid.y)*0.03)*0.5+0.5;
  ppp *= size;
  candy = mix(od(ppp,0.3),box(ppp,vec3(.4)), 0.5);

  vec2 id = round(p.xy/per);
  p.z = abs(p.z)-mix(0.01,4.,clamp(easeout(BPM/4.,length(id*0.02))*2.-1.,0.001,1.));
  vec3 pp = p; 

  vec2 bid=round(p.xy/per); 
  crep(p.xy,per,3.); 
  float d = max(dot(pp,normalize(vec3(0.,0.,-1.))),abs(mix(od(p,0.5),box(p,vec3(.6)), 0.5))-0.04);

  return min(d,candy);
}

vec3 getnorm (vec3 p, vec2 eps)
{return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

  vec3 ro = vec3(uv*10.,-500.), 
  rd=vec3(0.,0.,1.), 
  p=ro, 
  col=vec3(0.95,0.99,1.), 
  l=vec3(1.,-1.5,-2.);

  bool hit = false; float d=0.;
  for (float i=0.; i<ITER; i++)
  {
    d = SDF(p);
    if (d<0.01)
    {
      hit = true;
      break;
    }
    p += d*rd*0.9;
  }

  if (hit)
  {
    if (d==candy) col = vec3(0.8,hash21(cid),2.) ;
    vec3 n = getnorm(p,vec2(0.01,0.));
    float light = max(dot(n,normalize(l)),0.);
    col *= mix(vec3(0.5,0.3,0.5),vec3(0.4,0.7,0.9),light);
  }

  fragColor = vec4(sqrt(col),1.);
}
