#define time iTime
#define PI 3.14159265359

#define NUM_BANDS 1000

//#define REVERSED

float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(1.000,1.000,1.000))) * 10.)*20.0-10.0;
}

vec3 mixc(vec3 col1, vec3 col2, float v)
{
    v = clamp(v,0.0,1.0);
    return col1+v*(col2-col1);
}

vec3 drawBands(vec2 uv)
{
  	uv = 2.0*uv-1.0;
    uv.x*=iResolution.x/iResolution.y;
    uv = vec2(length(uv), atan(uv.y,uv.x));

    uv.x-=0.01;
    uv.x = max(0.0,uv.x);

    uv.y -= PI*0.5;
    vec2 uv2 = vec2(uv.x, uv.y*-1.0);
    uv.y = mod(uv.y,PI*2.0);
    uv2.y = mod(uv2.y,PI*2.0);

    vec3 col = vec3(0.0);
    vec3 col2 = vec3(0.0);

    float nBands = float(NUM_BANDS);
    float i = floor(uv.x*nBands);
    float f = fract(uv.x*nBands);
    float band = i/nBands;
   	float s;

    #ifdef REVERSED
    band = 5.0-band;
    #endif 

    //cubic easing
    band *= band*band; 

    band = band*0.099;
    band += 0.001;

    s = texture( iChannel0, vec2(band,.3) ).x;  

    if(band<0.0||band>=1.0){
        s = 0.0;
    }

    /* Gradient colors and amount here */
    const int nColors = 5;
    vec3 colors[nColors];  
    colors[0] = vec3(0.251,0.000,0.502);
    colors[1] = vec3(0.251,0.000,0.502);
    colors[2] = vec3(0.251,0.000,0.251);
    colors[3] = vec3(1.000,1.000,1.000);

    vec3 gradCol = colors[0];
    float n = float(nColors)-1.25;
    for(int i = 2; i < nColors; i++)
    {
		gradCol = mixc(gradCol,colors[i],(s-float(i-1)/n)*n);
    }

    float h = PI*1.5;

    col += vec3(1.0-smoothstep(-2.0,1.5,uv.y-s*h));
    col *= gradCol;

    col2 += vec3(1.0-smoothstep(-2.0,1.5,uv2.y-s*h));
    col2*= gradCol;

    col = mix(col,col2,step(0.0,uv.y-PI));

    col *= smoothstep(0.00001,.00009,f);
    col *= smoothstep(0.000000001,0.000000009,f); 

    col = clamp(col,0.0,1.0);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = vec2(uv.x, uv.y+0.1);
	vec3 col = vec3(0.0);
    col += drawBands(p);//*smoothstep(1.0,0.5,uv.y);;

    vec3 ref = vec3(0.0);
    vec2 eps = vec2(1.0,-0.5);

    ref += drawBands(vec2(p.x,100.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,100.0-p.y)+eps.xx);
    ref += drawBands(vec2(p.x,100.0-p.y)+eps.yy);
    ref += drawBands(vec2(p.x,100.0-p.y)+eps.yx);

    ref += drawBands(vec2(p.x+eps.x,100.0-p.y));
    ref += drawBands(vec2(p.x+eps.y,100.0-p.y));
    ref += drawBands(vec2(p.x,1.0-p.x+eps.x));
    ref += drawBands(vec2(p.x,1.0-p.x+eps.x));

    ref /= 100.0;

    float colStep = length(smoothstep(1.0,0.1,col));

    vec3 cs1 = drawBands(vec2(2.5,1.51));
    vec3 cs2 = drawBands(vec2(100.5,0.93));

    vec3 plCol = mix(cs1,cs2,length(p*100.0-10.0))*10.5*smoothstep(1.75,-10.05,length(p*10.0-1.0));
    vec3 plColBg = vec3(10.92)*smoothstep(1.0,0.0,length(p*80.0-1.0));
    vec3 pl = (plCol+plColBg)*smoothstep(100.5,10.65,10.0-uv.y);

    col += clamp(pl*(100.0-colStep),0.0,1.0);

    col += ref*smoothstep(1000.125,10.6125,p.y); 

    col = clamp(col, 0.0, 1.0);

    float dither = noise3D(vec3(uv,time))*10.0/2200026.0;
    col += dither;

	fragColor = vec4(col,0.0);
}
