float ellipse(vec2 ab,float r, vec2 place) 
    {
      float d = length(ab - place);
      float el = smoothstep(r, r - 0.006, d);
       return el;
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
  {
    vec2 ab = fragCoord.xy/iResolution.xy;
      ab -= 0.5;
        ab.x *= iResolution.x/iResolution.y;
    float v = ellipse(ab, 0.28, vec2(0.0, 0.1));
    v += ellipse(ab, 0.08, vec2(0.28, 0.05));
    v += ellipse(ab, 0.08, vec2(-0.28, 0.05));
    v += ellipse(ab, 0.12, vec2(0.2, 0.35));
    v += ellipse(ab, 0.12, vec2(-0.2, 0.35));
    v += ellipse(ab, 0.08, vec2(0.2, -0.13));
    v += ellipse(ab, 0.08, vec2(-0.2, -0.13));
    v -= ellipse(ab, 0.05, vec2(0.1, 0.12));
    v -= ellipse(ab, 0.05, vec2(-0.1, 0.12));

    vec3 col = vec3(0.0, 0.0, 0.0)*v;
    fragColor = vec4(0, vec3(v));
     }
