#define noise(p) sin(- p.x * 14. - p.y * 3.)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         p = (2.* u - R) / max(R.x, R.y) * 2.;

    for (float i = .1; i < .5; i+=.1 ) 
        p += .5* ( iTime*.2 + i* vec2(noise(p.xy),noise(p.yx)) );

    O = fract( vec4( 2.*p, p.x + p.y , 0 ) );
}
