#define PI 3.1415926538

float getAngle(vec2 v1)
{
    //return atan(v1.x,v1.y) -atan(v2.x,v2.y);
    return mod( atan(v1.x,v1.y) -atan(1.,0.), PI*2.)/PI/2.; //0 ... TWOPI
    //return mod( atan(v1.x,v1.y) -atan(v2.x,v2.y), TWOPI) - PI; //-pi to +pi 
}

vec2 stepper(vec2 p){

    float a = (getAngle(p)-0.5)*PI*20.;
    float lenStrands = 10.;
    float d = length(p)*lenStrands*0.5-lenStrands;


    vec2 xy = vec2(p.x,p.y);
    float x = xy.x;
    float y = xy.y+3.;

    x = a;
    y = d;




    float scalex = 1.;
    //float //xF = cos(x)*abs(sin(iTime)) + abs(sin(iTime))*2.*pow(sin(pow(y,1./2.)),1.);

    //float xF =  pow(cos(x+iTime),3.) - pow(cos(x-iTime/2.),1.) - pow(sin(y-iTime),3.) - pow(sin(y+iTime/2.),1.);
    float y3 = y-3.;
    float xF = sin(x/10.) + y3*y3*y3 - y;
    //xF = 0.;
    //xF += 3.;
    xF = 3. + xF*1. ;

    float yF = length(vec2(1.,y))*sin(x*2.+cos(y*1.+iTime)/1. + iTime );

    float eps = 0.1;

    //return vec2(smoothstep(xF,xF+eps,yF),xF);
    return vec2(smoothstep(0.,1.5*fwidth(yF-xF),yF-xF),xF);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-R/2.)/R.y;
    uv *= 10.;


    float a = getAngle(uv);



    vec2 vals = stepper(uv);

    vec3 col = vec3(vals.x);
    //col = vec3(a);

    col = pow(abs(col),vec3(1./2.2));



    // Output to screen
    fragColor = vec4(col,1.0);
}
