void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv_r = uv + vec2(sin(uv.x * 9.0f + iTime ) * 0.01, sin(uv.x + iTime) * 0.01);
    vec2 uv_g = uv + vec2(sin(uv.x + iTime ) * 0.008, sin(uv.x * 9.0f + iTime) * 0.01);
    vec2 uv_b = uv + vec2(sin(uv.y * 5.0f + iTime ) * 0.01, sin(uv.y* 5.0 + iTime) * 0.01);

    vec4 color_r = texture(iChannel0, uv_r) * vec4(1.0f,0.0f,0.0f,1.0f);
    vec4 color_g = texture(iChannel0, uv_g) * vec4(0.0f,1.0f,0.0f,1.0f);
    vec4 color_b = texture(iChannel0, uv_b) * vec4(0.0f,0.0f,1.0f,1.0f);

    fragColor = color_r + color_g + color_b;
}
