// Free for any purposes (that are not evil), forever

#define R 120.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;    

    vec2 s = vec2(R, R/(iResolution.x/iResolution.y));

    vec2 cell = ceil(uv*s)*(1.0/s);
    vec3 col = texture(iChannel0, cell).rgb;

    float v = pow((3.0 - (col.r + col.b + col.g)) * 0.21, 1.5);
    float c = smoothstep(v-0.02, v, length(fract(uv * s)-0.5)) * 0.8;
    col = vec3(c, c, c * 0.9);

    // Output to screen
    fragColor = vec4(col,1.0);
}
