#define T iTime 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv     = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col    = vec3(0.0);
    vec3 wusel  = 0.5 + 0.5*cos(iTime*0.3+abs(uv.xyx)+vec3(0,2,4));     // Color Wusel





    if ( mod(fragCoord.x,10.0)<=(sin((T+0.6)* 5.0)+1.2)*7.0 && 
         mod(fragCoord.y,10.0)<=(sin( T     * 5.0)+1.2)*7.0         ) 
    { 
         float brightness =     (sin((T+0.7)*10.0)*0.2)+0.8;
         col += wusel*brightness;//vec3(0.0,0.7,0.3)*brightness;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
