#define PI 3.1415926538

float getAngle(vec2 v1)
{
    //return atan(v1.x,v1.y) -atan(v2.x,v2.y);
    return mod( atan(v1.x,v1.y) -atan(1.,0.), PI*2.)/PI/2.; //0 ... TWOPI
    //return mod( atan(v1.x,v1.y) -atan(v2.x,v2.y), TWOPI) - PI; //-pi to +pi 
}

vec3 stepper(vec2 p){

    float a = (getAngle(p)-0.5)*PI*20.;
    float lenStrands = 4.;
    float d = length(p)*lenStrands*0.5-lenStrands;


    vec2 xy = vec2(p.x,p.y);
    float x = xy.x;
    float y = xy.y+3.;

    x = a;
    y = d;

    float center = max(y,-0.5);
    float c = smoothstep(.5,0.3,length(p));



    float xF = sin(x/10.)*18. + pow(y-3.,2.) - y;

    xF = 3. + xF*1. ;

    float yF = y*sin(x*2.+cos(y*1. + x/2. +iTime)/1. );


    return vec3(smoothstep(0.,1.5*fwidth(yF-xF),yF-xF),xF,yF);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-R/2.)/R.y;
    uv *= 15.;


    float a = getAngle(uv);
    float d = length(uv);




    vec3 vals = stepper(uv);

    float threshold = 0.8;
    vec2 loc1 = vec2(-2.,3.);
    vec2 uv1 = uv - loc1;
    float a1 = getAngle(uv1);
    float d1 = length(uv1);


    vec2 loc2 = vec2(2.,3.);
    vec2 uv2 = uv - loc2;
    float a2 = getAngle(uv2);
    float d2 = length(uv2);
    float diff2 = threshold + 0.5*(sin(a1*PI*8.+ iTime + PI/2.)+1.)/2. - d2;
    float eye2 = smoothstep(0.,1.5*fwidth(diff2),diff2);

        float diff1 = threshold + 0.5*(sin(a2*PI*6. + iTime)+1.)/2. - d1;
    float eye1 = smoothstep(0.,1.5*fwidth(diff1),diff1);

    vec3 color = sin(vec3(0.2,0.3,0.9)+vals.x + eye1 + eye2);

    vec3 col = vec3(1.)*color;


    // Output to screen
    fragColor = vec4(col,1.0);
}
