#define PI 3.1415926
mat2 rot(float c){
    float s=sin(c);
    return mat2(c=cos(c),s,-s,c);
}
float cros(vec2 p,vec2 d){
    p = abs(p); 
    return max(min(p.x,p.y)-d.y, max(p.x,p.y)-d.x);
}
void mainImage(out vec4 O,vec2 U){
    vec2 uv = (2.*U-iResolution.xy)/iResolution.y;

    float z = 6.; 
    uv *= z;
    float AA = z*2./iResolution.y;

    float t = 1.-fract(iTime/PI*2.5)*2.; 
    float s = step(0.,t);
    t = smoothstep(0.,1.,abs(t))*PI/2.;

    float r = length(vec2(1.5,.5));

    uv += s*vec2(2,1);

    uv *= rot(-atan(.5/1.5));
    uv = mod(uv,r*2.)-r;
    uv *= rot(t+atan(.5/1.5));

    float d = cros(uv,vec2(1.5,.5));

    O = mix(vec4(.2), vec4(1), smoothstep(-.75, .75, (s*2.-1.)*d/AA));
}
