vec4 tint_inner = vec4(1.0, 0, 0, 1.0);
vec4 tint_outer = vec4(0, 0.1, 0.1, 0.00001);
const float PI = 3.1415926;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float strength =  mod(iTime, 2.0*PI);//(1.0 + sin(iTime))/2.0;

	vec2 xy = fragCoord.xy / iResolution.xy;
    vec2 uv = xy;

    uv *=  1.0 - uv.yx;   //vec2(1.0)- uv.yx; -> 1.-u.yx; Thanks FabriceNeyret !

    float gradient = tanh(uv.x*uv.y * 200.0); // multiply with sth for intensity

    float vig = 1.0-pow(gradient,strength); // change pow for modifying the extend of the  vignette
    float tint = pow(gradient, pow(sin(iTime/3.0), 2.0)); // change pow for modifying the extend of the  vignette


    //fragColor = texture(iChannel0, xy) + vec4(vig); 
    fragColor = tint_inner*(1.0-vig) + tint_outer*vig + texture(iChannel0, xy)*(1.0-vig);

}
