void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col, col2, col3, col4;
    vec4 color;
    col = (fragCoord.x - iResolution.x / 2.0) * (fragCoord.y - iResolution.y / 2.0) * (150.0 + iTime / 300.0) / 10000.0;
    col2 = mod(col + iTime / 0.3 + fragCoord.x / iResolution.x * 50.0, 2.0) ;
    col3 = mod(col + iTime / 0.4 + fragCoord.y / iResolution.y * 50.0, 2.0) ;
    col4 = mod(col + iTime / 0.5 + fragCoord.y / iResolution.x * 50.0, 2.0) ;
    if (col2 > 1.0){ col2 = 2.0 - col2;}
    if (col3 > 1.0){ col3 = 2.0 - col3;}
    if (col4 > 1.0){ col4 = 2.0 - col4;}
    fragColor = vec4(col2, col3, col4, 1.0);
}
