void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float errorFromZero = sin((uv.x - iTime * 0.05) * 30.0) * 0.1 + 0.5 - uv.t;

    float amp = (sin(iTime * 2.0) * 0.2 - uv.x + 0.5);
    float gaussAmplify = 1.0 - pow(2.7, -4. * amp * amp);

    float greenColor = pow(2.7, -(50.0 + 2000.0 * gaussAmplify) * errorFromZero * errorFromZero);

    fragColor = vec4(0, greenColor, 0, 1.0);
}
