bool iterate(vec2 c, int maxIterations)
{
    int i = 0;
    vec2 z = vec2(0.0);

    while(length(z) < 2.0 && i < maxIterations)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        i++;
    }

    return i < maxIterations;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= vec2(2.0, 1.12) * vec2(2.0);
    uv -= (vec2(iMouse)/iResolution.xy - vec2(0.5)) * 2.0;


    //bool set = iterate(uv, 10);

    vec3 col = vec3(0.0);
    if(iterate(uv, 100))
    {
        col = vec3(1.0);
    }


    // Output to screen
    fragColor = vec4(col, 1.0);
}
