#define SCALE 0.25

vec3 background = vec3(1.0,1.0,0.0);
vec3 foreground = vec3(1.0,0.0,0.0);

vec3 square(vec2 uv, float size, vec2 offset) {
    float x = uv.x - offset.x;
    float y = uv.y - offset.y;
    float d = max(abs(x), abs(y)) - size;

    return d > 0. ? background : foreground;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // (0, 1
    uv -= 0.5; // (-0.5, 0.5)
    uv.x *= iResolution.x/iResolution.y; // Fix aspect

    // Color square
    vec3 col = square(uv, SCALE, vec2(0.0, 0.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
