void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;

    float r1 = length(uv - vec2(0.75,0.5 - 0.5*sin(iTime * 0.25)));

    float r2 = length(uv - vec2(0.75,0.5 + 0.5*sin(iTime * 0.25)));

    float f = sin(r1*60. - iTime * 2.1) + sin(r2*60.- iTime * 5.2);

    vec3 col = mix(vec3(0,0.5,1), vec3(0.5,0.25,0.1),f);

    // Output to screen
    fragColor = vec4(col,1.0);
}
