// Reference:
// Simple spiral https://www.shadertoy.com/view/MslyWB

const float pi = 3.141592654;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy; // 归一化坐标
  uv -= 0.5; // -1 to 1
  uv.x *= iResolution.x / iResolution.y; // aspect ratio

  float r = length(uv);
  float theta = atan(uv.y, uv.x);
  float c = smoothstep(fract(6.5 * theta / pi + 20.0 * pow(r, 0.2) - 1. * iTime), .9, .7);

  vec4 col = vec4(r, r, 0, 1.0) * .5 + vec4(1.0, 1.0, 1.0, 1.0) * .5;

  fragColor = mix(col, .6 - col, c);
}
