#define m(x) length(x + o + t * d) - 1.

void mainImage( out vec4 O, vec2 C ) {
    mat3 k = mat3(.1);

    vec3 R = iResolution,
         o = vec3(0,0,5),
         d = -vec3((C+C-R.xy)/3./R.y, 1);

    for (float f = (1.-o.y) / d.y, t = 0., i = t;
          i++<50. && (t += m())<1e2;
    O = sqrt(.5 + .5 * normalize(m()
        - vec4(m(k[0]), m(k[1]), m(k[2]), m()))))

    t = f > 0. ? min(t, f) : t;
}
