float random(vec2 uv){
    return fract(sin(uv.x*1600.+uv.y*6400.)*8000.);
}

vec2 random_location(int i){

    vec2 location = vec2(i);
    vec3 tran = location.xyx*vec3(123.,456.,89.);
    vec2 return_location = vec2(random(tran.xy),random(tran.yz));

    return return_location;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord /iResolution.xy;

    uv-=0.5;
    uv*=2.;
    float min_dist = 100.;

    for(int i = 0;i<50;i++){
        vec2 location = random_location(i);

        location = sin(location*iTime);

        float dt = length(location-uv);

        if(min_dist >dt){
            min_dist = dt;
        }
    }


    vec3 col = vec3(min_dist);

    fragColor = vec4(col,1.0);
}
