void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;

    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0,2,4));

    uv *= 32.0;

    float timeMultiplier = abs(16.0 - floor(uv.x)) + abs(8.0 - floor(uv.y)) + 1.0;

    uv = fract(uv);
    float x = uv.x;
    float y = uv.y;

    float time = fract(iTime * timeMultiplier / 16.0);

    float bottomOffset = -abs(8.0 * time - 1.0) + 1.0;
    float leftOffset = -abs(8.0 * time - 3.0) + 1.0;
    float topOffset = -abs(8.0 * time - 5.0) + 1.0;
    float rightOffset = -abs(8.0 * time - 7.0) + 1.0;

    if (y < bottomOffset) {
        fragColor = vec4(col, 1.0);
        return;
    } 
    if (x < leftOffset) {
        fragColor = vec4(col, 1.0);
        return;
    }
    if (y > 1.0 - topOffset) {
        fragColor = vec4(col, 1.0);
        return;
    }
    if (x > 1.0 - rightOffset) {
        fragColor = vec4(col, 1.0);
        return;
    }
    fragColor = vec4(0.0);
}
