#define PI     3.14159265358

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv.y += 0.4 * sin(uv.x * sin(iTime) + iTime);
    float scroll = 10. / 20.;
    vec2 noisePos = vec2(scroll + uv.x * 0.015, 0.5 + 0.5 * sin(scroll * PI));
    float numLines = 40. + 160. * sin(PI * texture(iChannel0, noisePos).r);
    float col = 0.4 + 0.05 * cos(uv.x * numLines);
    float aA = 1./(iResolution.y / numLines) ;
    vec4 textCol = texture(iChannel1, fragCoord.xy/iResolution.xy);
    float bwCol = dot(textCol.rgb, vec3(0.299, 0.587, 0.114));
    vec4 textBwCol = vec4(vec3(bwCol), 1.0);
	fragColor = mix(textBwCol, vec4(smoothstep(0.5 - aA, 0.5 + aA, col)), 0.08);
}
