vec2 hash2( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(113.5,271.9)));

	return fract(sin(p)*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 t = texture(iChannel0,uv).xyz;
    fragColor = vec4(t,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x+uv.y*10.0;
    t = step(t,0.5);

    t = step(length(uv-0.5),0.2);

    vec3 col = texture(iChannel0,uv*1.0).rgb;
    t = (col.r+col.g+col.b)*0.45;
    t = pow(t,5.0)*2.0;

    fragColor = vec4(t);
}

vec2 hash2( vec2 p ) //noise used for initial particle positions
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(113.5,271.9)));

	return fract(sin(p)*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = fragCoord/iResolution.y;
    vec2 noise = (hash2(uv)/iResolution.xy)*10.5;
    vec2 bnoise = texture(iChannel1,uv2).xy-0.5;
    float t;
    float t2 = texture(iChannel2,uv).r;
    float m = iMouse.x/iResolution.x;

    for(int i=0; i < 50; ++i){
        bnoise = (texture(iChannel1,uv2+hash2(vec2(float(i)+iTime))).xy-0.5);
        bnoise = normalize(bnoise);
        bnoise = bnoise*length(bnoise);
        bnoise = (bnoise/iResolution.xy)*m*500.;
        noise = ((hash2(uv+iTime)-0.5)/iResolution.xy)*m*500.;
        t = t + texture(iChannel0,uv+bnoise).r;
    }

    t = t/50.0;
    t = max(t,t2*0.9);

    fragColor = vec4(t);
}
