float speed = 5.;

vec2 rotate(in float angle, inout vec2 st)
{
    st -= .5;
    st = mat2(cos(angle), -sin(angle),
              sin(angle), cos(angle)) * st;
    st += .5;
    return st;
}

vec4 box(in vec2 size, in vec2 st) 
{
    size = vec2(.5) - size*.5;
    vec2 uv = smoothstep(size, size+vec2(.001), st);
    uv *= smoothstep(size, size+vec2(.001), vec2(1.)-st);

    return vec4(uv.x * uv.y);
}

vec4 cross(in float size, in vec2 st)
{
    return box(vec2(size, size/4.), st) + box(vec2(size/4., size), st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord.xy / iResolution.xy );
    uv.x *= 1.5;
    uv.x -= .25;

    uv.y += sin(iTime) * .1;
    uv.x += cos(iTime) * .1;

    uv = rotate(iTime * speed, uv);

    fragColor = cross(.25, uv);
}
